            border-top-right-radius: 50px;
            border-bottom-right-radius: 50px;
            font-size: 16px;
            transition: all 0.2s ease-in-out;
        }
        #profileButtonsDiv button:hover
        {
            background-color: var(--primary);
            color: white;
        }
        #profileButtonsDiv button.active{
            background-color: var(--primary);
            color: white;
        }
    </style>
@endsection

